import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');

  const fetchData = async () => {
    try {
      document.getElementById('message-display').innerText = "Fetching message...";

      const response = await axios.get('http://localhost:3001');
      setMessage(response.data);

      document.getElementById('message-display').innerText = data.message;
    } catch (error) {
      console.error("Error fetching data:", error);
      document.getElementById('message-display').innerText = "Failed to fetch message.";
    }
  };

  document.getElementById('fetch-button').addEventListener('click', fetchMessage);

  return (
    <div className="App">
      <h1>Music Album Review Site</h1>
      <button onClick={fetchData}>Fetch Message</button>
      <p id="message-display">{message}</p>
    </div>
  );
}



export default App;
