import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [fetchStatus, setFetchStatus] = useState('');

  const fetchData = async () => {
    setFetchStatus('Fetching...');
    try {
      const response = await axios.get('http://localhost:3001'); // Ensure this URL matches your backend
      setMessage(response.data);
      setFetchStatus('Message fetched successfully!');
    } catch (error) {
      console.error("Error fetching data:", error);
      setFetchStatus('Error fetching message.');
    }
  };

  useEffect(() => {
    document.getElementById('fetch-button').addEventListener('click', fetchData);
    return () => {
      document.getElementById('fetch-button').removeEventListener('click', fetchData);
    };
  }, []); // Run only once on mount

  return null; // Since we're using index.html for rendering
}

export default App;
