import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState(''); // Message received from backend
  const [inputMessage, setInputMessage] = useState(''); // Message to send to backend
  const [fetchStatus, setFetchStatus] = useState(''); // Status of fetch request

  const fetchData = async () => {
    setFetchStatus('Fetching...');
    try {
      const response = await axios.get('http://localhost:3001');
      setMessage(response.data); // Set the received message
      setFetchStatus('Message fetched successfully!');
    } catch (error) {
      console.error("Error fetching data:", error);
      setFetchStatus('Error fetching message.');
    }
  };

  const sendMessage = async () => {
    setFetchStatus('Sending message...');
    try {
      await axios.post('http://localhost:3001/message', { message: inputMessage }); // Send the message to the backend
      setFetchStatus('Message sent successfully!'); // Update status on success
      setInputMessage(''); // Clear the input field
    } catch (error) {
      console.error("Error sending message:", error);
      setFetchStatus('Error sending message.'); // Update status on error
    }
  };

  return (
    <div className="App">
      <h1>Music Album Review Site</h1>

      <button onClick={fetchData}>Fetch Message</button>
      <p>{fetchStatus}</p>
      <p>{message}</p>

      <input
        type="text"
        value={inputMessage}
        onChange={(e) => setInputMessage(e.target.value)} // Update inputMessage on change
        placeholder="Type your message here"
      />
      <button onClick={sendMessage}>Send Message</button>
    </div>
  );
}

export default App;
