import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000');
      setMessage(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="App">
      <h1>Music Album Review Site</h1>
      <button onClick={fetchData}>Fetch Message</button>
      <p>{message}</p>
    </div>
  );
}

const fetchMessage = async () => {
  try {
    const response = await fetch('http://localhost:5000/api/message');
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const data = await response.json();
    console.log(data.message); // Check console for the fetched message

    // Update the UI with the fetched message
    document.getElementById('message-display').innerText = data.message;
  } catch (error) {
    console.error('Fetch error:', error);
  }
};

// Add an event listener to the button to fetch the message when clicked
document.getElementById('fetch-button').addEventListener('click', fetchMessage);


export default App;
