import React, { useState } from 'react';
import axios from 'axios';

function App() {
  //const [message, setMessage] = useState('');

  const fetchMessage = async () => {
    // Display a message indicating the button was clicked
    document.getElementById('message-display').innerText = "Fetching message...";

    try {
      const response = await fetch('http://localhost:5000/api/message');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json(); // Make sure 'data' is defined here

      // Display the fetched message
      document.getElementById('message-display').innerText = data.message;
    } catch (error) {
      console.error('Fetch error:', error);
      // Display an error message in the UI
      document.getElementById('message-display').innerText = "Failed to fetch message.";
    }
  };

  // Add an event listener to the button to fetch the message when clicked
  document.getElementById('fetch-button').addEventListener('click', fetchMessage);
  return (
    <div className="App">
      <h1>Welcome to MusicBox</h1>
      <button id="fetch-button">Fetch Message</button>
      <div id="message-display"></div>
    </div>
  );
}



export default App;
